{
  "functions": {
    "backend/app/main.py": {
      "runtime": "python3.9",
      "memory": 1024,
      "maxDuration": 10
    }
  },
  "build": {
    "env": {
      "PIP_REQUIRE_VIRTUALENV": "0"
    }
  },
  "routes": [
    {
      "src": "/api/(.*)",
      "dest": "backend/app/main.py"
    },
    {
      "src": "/(.*)",
      "dest": "frontend/$1"
    }
  ],
  "rewrites": [
    {
      "source": "/(.*)",
      "destination": "/frontend/$1"
    }
  ]
}

